/*+++ 2016.09.07 +++*/

/*文本选中样式（黑底+绿字）*/
*::-moz-selection {
/* the background color of the highlight */
	background: #333333 !important;
/* the color of the text within the highlight */
	color: #00FF00 !important;
}

/*改变已阅读链接字体样式*/
@-moz-document
url-prefix("http"),url-prefix("https"),url-prefix("file") {
	a:visited, a:visited *, a:active, a:active * {
		color: rgb(130,130,130) !important;
/*灰色*/
	}
}

/*超链接字体颜色渐变*/
@-moz-document url-prefix("http"),url-prefix("https"),url-prefix("file") {
	a:hover,a:hover em,a>font:hover,b>font:hover,a:hover *,button[type="submit"]:hover *,label:hover {
		transition-property: color,text-shadow!important;
		transition-duration: 0.423s!important;
		transition-timing-function: ease!important;
		color: rgba(255,200,0,60)!important;
		text-shadow: 0 1px rgba(242,33,49,60),0 1px 1px rgba(242,33,49,60),0 -1px 1px rgba(242,33,49,60),
1px 0 1px rgba(242,33,49,60),-1px 0 1px rgba(242,33,49,60),0 0 3px rgba(242,33,49,60)!important;
	}
}

/*+++2017.02.27+++*/

/* Textareas - Resize always */
html textarea {
	resize: both !important;
}

/*清除网页干扰码*/
body {
	-o-user-select: element !important;
}

SPAN.fw,/*国学网*/
font.jammer,
font[style*="font-size: 0px;"] {
	display: none !important
}


/* 阻止<blink>标签带来的闪烁 */
blink, .blink, *[style*="blink"] {
	text-decoration: inherit !important;
}

/*显示丢失图片*/
@-moz-document url-prefix(http), url-prefix(https), url-prefix(file) {
	img {
		font: 12px Arial!important;
	}

	img:-moz-broken {
		-moz-force-broken-image-icon: 1 !important;
		width: 48px;
		height: 24px;
	}
}

/*图片经过外发光*/
img:hover {
	box-shadow: 0px 0px 4px 4px rgba(130,190,10,0.6) !important;
	-moz-transition-property: box-shadow;
	-moz-transition-duration: .31s;
}

img {
	-moz-transition-property: box-shadow;
	-moz-transition-duration: .31s;
}

