/* 顶部尺寸/header top size */
div#root>div>div#header{height: auto !important;z-index: 9999 !important;}
#account>a>span{padding: 10px !important;margin-left: inherit !important;}
#top_logo>a>img{width: 70px !important;}
#create_new_style{min-height: 0px !important;}
div#root>div>#header>div#search_input{height: auto !important;}
/* 左侧导航设置/left side navigation set */
