div#container>div#nav-view>div#navigation{padding: 0 !important;width: 12px !important;}
div#container>div#nav-view>div#navigation{position: absolute !important;left: -16vw !important;top: 10vh !important;}
#view{background-color: #f5f7fa !important;z-index: 1001 !important;}
/* 左侧导航宽度/left side navigation width */div#nav-view>div#navigation>div:nth-child(n){width: 70% !important;overflow: hidden !important;}
/* 左侧导航字体/left side navigation font*/div#container>div#nav-view>div#navigation *{font-family: "Segoe UI","Arial","Microsoft YaHei"   !important;}

#footer{position: absolute !important;margin-top: 280vh !important;}
/* 杂项设置 */
div#nav-view{position: absolute !important;left: 16vw !important;top: 1vh !important;width: 82% !important;}
#view_inner{width: 100vw !important;}
/* 兼容FireFox */
@supports(-moz-animation: none){
#footer{position: absolute !important;margin-top: 370vh !important;}
}

